rule.example:
// Bad
public class A { }
// Good
public class A { private int x; }

// Source: https://example.com/my-sketch
// This sketch is based on the tutorial at the link above

void setup() {
    size(400, 400);
}

void draw() {
    background(255);
}

// Sketch should compile without errors
void setup() {
    size(400, 400);
}

void draw() {
    background(255);
}

// Missing draw() -> ❌ flagged
void setup() {
    size(400, 400);
}

// Missing setup() -> ❌ flagged
void draw() {
    background(255);
}

// Incorrect signature -> ❌ flagged
int setup(int x) { return 0; }
void draw(int t) { /* wrong signature */ }

class Ball {
    int x, y;
    Ball(int x, int y) {
        this.x = x;
        this.y = y;
    }
}

void main() {
    Ball b = new Ball(100, 200); // ✅ used
}


class Example {
    // ✅ This constructor satisfies the rule
    Example(int x, int y) {
        // ...
    }
}


class Main {
    void main() {
        Ball b = new Ball();
    }
    class Ball {
        int x;
        int y;
    }
}


