if (x > 5) {
    println("Greater");
} // ❌ flagged (no else)
if (x > 5) {
    println("Greater");
} else {
    println("Not greater");
} // ✅ not flagged

if (x > 5 && y < 10) { // ✅ not flagged
    println("Valid");
}
if (x > 5) { // ❌ flagged (no boolean operator)
    println("Greater");
}

class Outer {
    class Middle {
        class Inner {
            // ❌ flagged (triple nested)
        }
    }
}
class NotNested {} // ✅ not flagged
class SingleNested {
    class Inner {} // ✅ not flagged
}

void mousePressed() {
    // No method calls // ❌ flagged
}
void mouseReleased() {
    println("Hello"); // ❌ flagged (if println is not declared)
}
void mouseDragged() {
    customMethod(); // ✅ not flagged (if customMethod is declared)
}
void customMethod() {
    // ...
}

void mousePressed() {
    // No control flow // ❌ flagged
}
void mouseReleased() {
    for (int i = 0; i < 10; i++) { println(i); } // ✅ not flagged
}
void mouseDragged() {
    if (true) { println("Hello"); } // ✅ not flagged
}

class Outer {
    class Inner {
        void someMethod() {
            println("Hello"); // ❌ flagged (no draw method called)
        }
    }
}
class Outer2 {
    class Inner2 {
        void someMethod() {
            draw(); // ✅ not flagged
        }
    }
}

// ✓ PASS - has control flow
void mousePressed() {
    if (mouseX > width/2) {
        background(255);
    }
}

// ✓ PASS - calls declared method
void mousePressed() {
    doSomething();
}

// ✗ FAIL - too simple
void mousePressed() {
    x = 10;  // Just assignment, no control flow or method calls
}

int[] arr = {1,2,3};
for (int i = 0; i < arr.length; i++) {
    println(arr[i]); // ✅ not flagged
}
for (int n : arr) {
    println(n); // ❌ flagged
}

// ✓ PASS - class with constructor
class Ball {
    int x, y;

    Ball(int x, int y) {
        this.x = x;
        this.y = y;
    }
}

// ✗ FAIL - class without constructor
class Ball {
    int x, y;
    // No constructor!
}

// ✗ FAIL - no classes at all
void setup() {
    size(400, 400);
}

// ✗ FAIL - using inheritance
class Animal {
    String name;
}

class Dog extends Animal {
    // This will be flagged
}

// ✓ PASS - no inheritance
class Ball {
    int x, y;

    Ball(int x, int y) {
        this.x = x;
        this.y = y;
    }
}
